mapper.insert:

    SELECT RECALL_SEQ.NEXTVAL FROM DUAL

INSERT INTO RECALL (
ID, MAKER, MODEL_NAME, MAKE_START, MAKE_END,
RECALL_DATE, RECALL_REASON
) VALUES (
#{id},
#{maker}, #{modelName}, #{makeStart}, #{makeEnd},
#{recallDate}, #{recallReason}
)

mapper.select:
SELECT ID, MAKER, MODEL_NAME, MAKE_START, MAKE_END, RECALL_DATE, RECALL_REASON
FROM (
    SELECT ROWNUM AS rn, ID, MAKER, MODEL_NAME, MAKE_START, MAKE_END, RECALL_DATE, RECALL_REASON
    FROM (
        SELECT ID, MAKER, MODEL_NAME, MAKE_START, MAKE_END, RECALL_DATE, RECALL_REASON
        FROM RECALL


                UPPER(MAKER) LIKE '%' || UPPER(#{keyword}) || '%'
                OR UPPER(MODEL_NAME) LIKE '%' || UPPER(#{keyword}) || '%'


        ORDER BY ID DESC
    )
    WHERE ROWNUM <= #{pageNum} * #{amount}
)
WHERE rn > (#{pageNum} - 1) * #{amount}
